import React from "react";
import HeroBox from "../components/HeroBox";
import ProductsCard from "../components/productsCard";

const indexPage = () => {
  const popularProducts = {
    title: "Shoes Reviews",
    products: [
      {
        // title: "Running Shoes",
        image: "/images/1.jpg",
      },
      {
        // title: "Women Boots",
        image: "/images/2.jpg",
      },
      {
        // title: "Kids Footwear",
        image: "/images/3.jpg",
      },
      {
        // title: "Women Mules",
        image: "/images/4.jpg",
      },
      {
        // title: "Men Sandals",
        image: "/images/5.jpg",
      },
      // {
      //   // title: "Men Boots",
      //   image: "/images/nike2.jpg",
      // },
    ],
  };

  const favoriteProducts = {
    title: "Featured Daily Deals",
    products: [
      {
        title: "Our Comfeist Footwear",
        image: "/images/fdd1.jpg",
        caption: "Enjoy popular styles that looks and feel good!",
        explore: "Explore Comfort Shoes",
      },
      {
        title: "Slide Sandals",
        image: "/images/fdd2.jpg",
        price: "On-trend styles that truly let you feel relax!",
        offPrice: "Explore Slide Sandals",
      },

      {
        title: "Sandals for Kids",
        image: "/images/fdd3.jpg",
        caption: "Perfect for backyard adventures and playtime!",
        explore: "Explore Kids Sandals",
      },
    ],
  };
  const brandHeaders = {
    title: "Trending Brands",
    products: [
      {
        title: "Clarks",
        image: "/images/headers/clarks.jpg",
      },
      {
        title: "Nike",
        image: "/images/headers/nike.jpg",
      },
      {
        title: "Nike Kids",
        image: "/images/headers/nikekids.jpg",
      },
      {
        title: "Converse",
        image: "/images/headers/converse.jpg",
      },
      {
        title: "UGG",
        image: "/images/headers/ugg.jpg",
      },
      {
        title: "Brooks",
        image: "/images/headers/brooks.jpg",
      },
    ],
  };

  return (
    <React.Fragment>
      <HeroBox />
      <ProductsCard
        productsList={popularProducts}
        flexBasis="20%"
        shadow={true}
        type="small"
      />
      <ProductsCard
        productsList={favoriteProducts}
        flexBasis="33%"
        shadow={false}
        type="large"
      />
      <ProductsCard
        productsList={brandHeaders}
        flexBasis="16%"
        shadow={true}
        type="small"
      />
    </React.Fragment>
  );
};

export default indexPage;
