import React from "react";

const product = () => {
  const product = {
    name: "Nike Men Zoom Fly SP",
    images: [
      "/images/p1.jpg",
      "/images/p2.jpg",
      "/images/p3.jpg",
      "/images/p4.jpg",
      "/images/p5.jpg",
      "/images/p6.jpg",
    ],
    price: 49.99,
    sizes: ["Men UK6", "Men UK7", "Men UK7.5", "Men UK8", "Men UK8.5"],
  };

  return (
    <div className="product">
      <form>
        <div className="product-wrapper">
          <div className="product-images-wrapper">
            {product.images.map((image, index) => (
              <img src={image} key={index} />
            ))}
          </div>
          <div className="checkout"></div>
        </div>
      </form>
      <style jsx>{`
        .product {
          width: 100%;
          max-width: 1400px;
          margin: 50px auto;
          position: relative;
        }
        .product-wrapper {
          display: flex;
          justify-content: space-between;
        }
        .product-images-wrapper {
          width: 60%;
          display: flex;
          justify-content: flex-start;
          flex-wrap: wrap;
        }

        .product-images-wrapper img {
          flex-basis: 49.5%;

          //   border-bottom: 1px solid #575757;
        }
        .product-info ul li {
          list-style: disc;
          margin-top: 8px;
        }
        .checkout {
          width: 20%;
          display: flex;
          flex-direction: column;
          //   border: 1px solid;
          background-color: #eee;
          align-items: center;
          padding: 20px;
          max-height: 500px;
        }
        .checkout label {
          width: 100%;
          margin-top: 10px;
          line-height: 25px;
        }
        .checkout label select {
          width: 100%;
          margin-top: 5px;
          border-radius: 5px;
          font-size: 14px;
          color: #575757;
        }
        .price {
          color: #b12704;
        }
        .add-cart {
          margin-top: 20px;
          background-color: #ffdb00;
          border: 1px solid #ffdb00;
          border-radius: 5px;
          width: 100%;
          padding: 10px 0px;
          text-align: center;
          font-weight: bold;
          font-size: 16px;
          cursor: pointer;
        }
      `}</style>
    </div>
  );
};

export default product;
