import React, { Component } from "react";

import classes from "./productForm.module.css";
import Input from "../../../UI/Input/Input";
import Button from "../../../UI/Button/Button";

class ProductForm extends Component {
  state = {
    ProductForm: {
      title: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "Product Title",
        },
        value: "",
      },
      price: {
        elementType: "input",
        elementConfig: {
          type: "number",
          placeholder: "Product Price",
        },
        value: "",
      },
      size: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "Size",
        },
        value: "",
      },

      color: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "Color",
        },
        value: "",
      },

      material: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "Product Material",
        },
        value: "",
      },
      feature: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "Product Feature",
        },
        value: "",
      },
      category: {
        elementType: "select",
        elementConfig: {
          options: [
            { value: "men", displayValue: "Men" },
            { value: "women", displayValue: "Women" },
            { value: "kids", displayValue: "Kids" },
          ],
        },
        value: "",
      },

      subCategory: {
        elementType: "select",
        elementConfig: {
          options: [
            { value: "shoes", displayValue: "Shoes" },
            { value: "women", displayValue: "Women" },
            { value: "Kids", displayValue: "Kids" },
          ],
        },
        value: "",
      },

      brand: {
        elementType: "select",
        elementConfig: {
          options: [
            { value: "nike", displayValue: "Nike" },
            { value: "clarks", displayValue: "Clarks" },
            { value: "Gucci", displayValue: "Gucci" },
          ],
        },
        value: "",
      },
      description: {
        elementType: "textarea",
        elementConfig: {
          placeholder: "Product description",
        },
        value: "",
      },
    },
  };
  inputChangeHandler = (event, inputIdentifier) => {
    const updatedProductForm = { ...this.state.ProductForm };
    const updatedFormElement = { ...updatedProductForm[inputIdentifier] };
    updatedFormElement.value = event.target.value;
    updatedProductForm[inputIdentifier] = updatedFormElement;
    this.setState({ ProductForm: updatedProductForm });
  };
  render() {
    let search = null;
    if (this.props.update) {
      search = (
        <div>
          <form className={classes.search}>
            <input type="text" placeholder="Enter Product Code" />
            <input type="submit" value="Search" />
          </form>
        </div>
      );
    }

    const formElementArray = [];
    for (let key in this.state.ProductForm) {
      formElementArray.push({
        id: key,
        config: this.state.ProductForm[key],
      });
    }
    return (
      <div className={classes.FormsWrapper}>
        <div className={classes.Form}>
          <h2>{this.props.title}</h2>
          {search}
          <form>
            {/* {this.props.type == "Update Product" ? <} */}
            {formElementArray.map((formElement, index) => (
              <Input
                key={formElement.id}
                elementType={formElement.config.elementType}
                elementConfig={formElement.config.elementConfig}
                value={formElement.config.value}
                changed={(event) =>
                  this.inputChangeHandler(event, formElement.id)
                }
              />
            ))}

            <Button>Add Product</Button>
          </form>
        </div>
      </div>
    );
  }
}

export default ProductForm;
