import React, { Component } from "react";

import classes from "./productForm.module.css";

class ProductForm extends Component {
  state = {
    ProductForm: {
      title: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "Product Title",
        },
        value: "",
      },
      price: {
        elementType: "input",
        elementConfig: {
          type: "number",
          placeholder: "Product Price",
        },
        value: "",
      },
      size: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "Size",
        },
        value: "",
      },

      color: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "Color",
        },
        value: "",
      },

      material: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "Product Material",
        },
        value: "",
      },
      feature: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "Product Feature",
        },
        value: "",
      },
      category: {
        elementType: "select",
        elementConfig: {
          options: [
            { value: "men", displayValue: "Men" },
            { value: "women", displayValue: "Women" },
            { value: "kids", displayValue: "Kids" },
          ],
        },
        value: "",
      },

      subCategory: {
        elementType: "select",
        elementConfig: {
          options: [
            { value: "shoes", displayValue: "Shoes" },
            { value: "bags", displayValue: "Women" },
            { value: "Kids", displayValue: "Kids" },
          ],
        },
        value: "",
      },

      brand: {
        elementType: "select",
        elementConfig: {
          options: [
            { value: "nike", displayValue: "Nike" },
            { value: "clarks", displayValue: "Clarks" },
            { value: "Gucci", displayValue: "Gucci" },
          ],
        },
        value: "",
      },
      description: {
        elementType: "textarea",
        elementConfig: {
          placeholder: "Product Price",
        },
        value: "",
      },
    },
  };

  render() {
    let search = null;
    if (this.props.update) {
      search = (
        <div>
          <form className={classes.search}>
            <input type="text" placeholder="Enter Product Code" />
            <input type="submit" value="Search" />
          </form>
        </div>
      );
    }

    let formElementArray = [];
    for (let key in this.state.ProductForm) {
      formElementArray.push({
        id: key,
        config: this.state.ProductForm[key],
      });
    }
    return (
      <div className={classes.FormsWrapper}>
        <div className={classes.Form}>
          <h2>{this.props.title}</h2>
          {search}
          <form>
            {/* {this.props.type == "Update Product" ? <} */}
            {formElementArray.map((formElement, index) => (
              <Input
                key={formElement.id}
                elementType={formElement.config.elementType}
                elementConfig={formElement.config.elementConfig}
                value={formElement.config.value}
              />
            ))}
            <div>
              <input type="text" name="title" placeholder="Product Title" />
              <input type="number" name="price" placeholder="Product Price" />
            </div>
            <div>
              <select name="category">
                <option value="">---Select Category</option>
                <option value="male">Male</option>
                <option value="female">Female</option>
                <option value="kids">Kids</option>
              </select>

              <select name="subCategory">
                <option value="">---Select Product Sub-Category</option>
                <option value="shoes">Shoes</option>
                <option value="activities">Activities</option>
                <option value="clothes">CLothes & More</option>
              </select>
            </div>
            <div>
              <select name="brand">
                <option value="">---Select Product Brand</option>
                <option value="nike">Nike</option>
                <option value="addidas">Addidas</option>
                <option value="clarks">Clarks</option>
                <option value="etc">etc...</option>
              </select>

              <input type="number" name="quantity" placeholder="Quantity" />
            </div>
            <div>
              <input type="number" name="sizes" placeholder="Sizes" />
              <input type="text" name="colors" placeholder="Colors" />
            </div>

            <div>
              <input type="text" name="material" placeholder="Material" />
              <input type="text" name="feature" placeholder="Feature" />
            </div>

            <textarea
              name="description"
              placeholder="Product description"
            ></textarea>

            <input type="submit" value={this.props.title} />
          </form>
        </div>
      </div>
    );
  }
}

export default ProductForm;
